import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventsList = () => {
  const [events, setEvents] = useState([]);

  // Function to fetch data from the Flask API
  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  // UseEffect to fetch data every 15 seconds
  useEffect(() => {
    // Initial fetch
    fetchEvents();

    // Polling every 15 seconds
    const interval = setInterval(() => {
      fetchEvents();
    }, 15000);

    // Clean up interval on component unmount
    return () => clearInterval(interval);
  }, []);

  

  return (
    <div>
      <h2>Recent GitHub Events</h2>
      <ul>
        {events.map((event, index) => (
          
          <li key={index}>
            {event.action === 'push' && (
              <span><b>"{event.author}"</b> pushed to <b>"{event.to_branch}"</b> on <b>{Date(event.timestamp)}</b></span>
            )}
            {event.action === 'pull_request' && (
              <span><b>"{event.author}"</b> submitted a pull request from <b>"{event.from_branch}"</b> to <b>"{event.to_branch}"</b> on <b>{event.timestamp}</b></span>
            )}
            {event.action === 'merge' && (
              <span><b>"{event.author}"</b> merged branch <b>"{event.from_branch}"</b> to <b>"{event.to_branch}"</b> on <b>{event.timestamp}</b></span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventsList;
