import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventsList = () => {
  const [events, setEvents] = useState([]);

  // Function to fetch data from the Flask API
  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  // UseEffect to fetch data every 15 seconds
  useEffect(() => {
    // Initial fetch
    fetchEvents();

    // Polling every 15 seconds
    const interval = setInterval(() => {
      fetchEvents();
    }, 15000);

    // Clean up interval on component unmount
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h2>Recent GitHub Events</h2>
      <ul>
        {events.map((event, index) => (
          <li key={index}>
            {event.action === 'push' && (
              <span>"{event.author}" pushed to "{event.to_branch}" on {event.timestamp}</span>
            )}
            {event.action === 'pull_request' && (
              <span>{event.author} submitted a pull request from {event.from_branch} to {event.to_branch} on {event.timestamp}</span>
            )}
            {event.action === 'merge' && (
              <span>{event.author} merged branch {event.from_branch} to {event.to_branch} on {event.timestamp}</span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventsList;
